//广度优先搜索package DFS;import java.util.ArrayList;import java.util.LinkedList;import java.util.List;import java.util.Queue;public class bfs {    public List<Integer> bfsSearch(TreeNode root) {        List<Integer> list = new ArrayList<>();        Queue<TreeNode> queue = new LinkedList<>();        TreeNode cur = root;        while (cur != null || !queue.isEmpty()){            if (cur != null){                list.add(cur.val);                queue.add(cur.left);                queue.add(cur.right);                cur = queue.poll();            }else{                cur = queue.poll();            }        }        return list;    }}